import { createTheme, Grid } from "@mui/material";
import GlobalStyles from "@mui/material/GlobalStyles";
import { LightSwitch } from "./components/LightSwitch";

const customTheme = createTheme({
  typography: {
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(","),
  },
  colors: {
    on: "#8CBBE9",
    off: "#ECECEC",
  },
});

function App() {
  return (
    <>
      <GlobalStyles
        styles={(customTheme) => ({
          body: {
            fontFamily: customTheme.typography.fontFamily,
            display: "flex",
            flexDirection: "row",
            justifyContent: "center",
            alignItems: "center",
          },
        })}
      />
      <h1>IoT Hydroponics Project</h1>

      <Grid container spacing={2}>
        <Grid item xs={8}>
          <LightSwitch room={"ROOM 1"} />
        </Grid>
        <Grid item xs={4}>
          <LightSwitch room={"ROOM 2"} />
        </Grid>
      </Grid>
    </>
  );
}

export default App;
